mapper.select:
select b.* from (select rownum rn, a.* from (SELECT supplier.sup_id,supplier.sup_name,order_Form.order_id,order_Form.order_type_id, product.shop_id,product.shop_name,product.cost, order_Form.shop_num, (product.cost*order_Form.shop_num),product_class_two.pct_id, product_class_two.pct_name,product_class_one.pco_id,product_class_one.pco_name
FROM supplier,product,product_class_two,product_class_one,order_Form
WHERE order_Form.order_state_id = 1 AND order_Form.shop_id = product.shop_id AND product.p_pct_id=product_class_two.pct_id AND product_class_two.belong_id=product_class_one.pco_id AND product.supplier_id = supplier.sup_id and order_Form.order_id NOT IN (SELECT allocate_id FROM io_allocate)) a) b where b.rn > (#{pageNum}-1)*#{pageCounts} AND b.rn <= #{pageNum}*#{pageCounts}
and order_Form.order_id=#{orderform.orderId}

SELECT supplier.sup_id,supplier.sup_name,order_Form.order_id,order_Form.order_type_id, product.shop_id,product.shop_name,product.cost, order_Form.shop_num, (product.cost*order_Form.shop_num),product_class_two.pct_id, product_class_two.pct_name,product_class_one.pco_id,product_class_one.pco_name
FROM supplier,product,product_class_two,product_class_one,order_Form
WHERE order_Form.order_state_id = 1 AND order_Form.shop_id = product.shop_id AND product.p_pct_id=product_class_two.pct_id AND product_class_two.belong_id=product_class_one.pco_id AND product.supplier_id = supplier.sup_id and order_Form.order_id=#{orderform.orderId}

SELECT supplier.sup_id,supplier.sup_name,order_Form.order_id,order_Form.order_type_id, product.shop_id,product.shop_name,product.cost, order_Form.shop_num, (product.cost*order_Form.shop_num),product_class_two.pct_id, product_class_two.pct_name,product_class_one.pco_id,product_class_one.pco_name
FROM supplier,product,product_class_two,product_class_one,order_Form
WHERE order_Form.order_type_id = 4 AND order_Form.shop_id = product.shop_id AND product.p_pct_id=product_class_two.pct_id AND product_class_two.belong_id=product_class_one.pco_id AND product.supplier_id = supplier.sup_id and order_Form.order_id=#{orderform.orderId}
